import { useState } from 'react'

const App = () => {
  const [persons, setPersons] = useState([
    { name: 'Arto Hellas' }
  ]) 
  const [newName, setNewName] = useState('')

  const checkName = () => persons.includes({newName})

  const addNumber = checkName() ? alert(`${newName} already exists`) :
    (event) => {
    event.preventDefault()
    const numberObject = {
      name: newName
    }
    setPersons(persons.concat(numberObject))
    setNewName("")
  }


  const handleNewName = (event) => {
    setNewName(event.target.value)
  }

  const Person = ({person}) => {
    return(
      <li>
        {person.name}
      </li>
    )
  }

  return (
    <div>
      <h2>Phonebook</h2>
      <form onSubmit={addNumber}>
        <div>
          name: <input 
          value = {newName}
          onChange = {handleNewName}/>
        </div>
        <div>
          <button type="submit">add</button>
        </div>
      </form>
      <h2>Numbers</h2>
      <ul>
        {persons.map(person =>
          <Person key = {person.name} person = {person}/>)}
      </ul>
    </div>
  )

}

export default App
