import { Component, useState } from 'react'
import Filter from './components/filterform.js'
import PersonForm from './components/personform.js'
import PersonList from './components/personlist.js'

const App = () => {
  const [persons, setPersons] = useState([])  

  const [newName, setNewName] = useState('')
  const [newNumber, setNewNumber] = useState('')
  const [newFilter, setNewFilter] = useState('')

  const filteredPersons = persons.filter(person => person.name.toLocaleLowerCase().includes(newFilter.toLowerCase()))

  const addNumber = (event) => {
    event.preventDefault()
    const numberObject = {
      name: newName,
      number: newNumber
    }
    const names = persons.map(person => person.name)
    names.includes(numberObject.name)
    ? alert(`${newName} is already added to phonebook`)
    : setPersons(persons.concat(numberObject))
    setNewName("")
    setNewNumber("")
  }

  const handleNewName = (event) => {
    setNewName(event.target.value)
  }

  const handleNewNumber = (event) => {
    setNewNumber(event.target.value)
  }

  const handleNewFilter = (event) => {
    setNewFilter(event.target.value)
  }

  return (
    <div>
      <h2>Phonebook</h2>
      <p>
        <Filter 
        newFilter={newFilter} 
        handleNewFilter={handleNewFilter} 
        />
      </p>
      <h2>Add a new</h2>

      <PersonForm 
      addNumber={addNumber} 
      newName = {newName} 
      handleNewName = {handleNewName}
      newNumber = {newNumber} 
      handleNewNumber = {handleNewNumber}
      />

      <h2>Numbers</h2>
      <PersonList filteredPersons={filteredPersons} />
    </div>
  )

}

export default App
